@InProceedings{Dalchand2015, author= {Dalchand, Elizabeth
and Tsuno, Sayaka
and Scorese, Alison
and Francisco, Karol
and Buzard, Camilla
and Merrer, Dina C.}, title= {Phenylchlorocarbene additions to diarylcyclooctynes and diarylcyclooctenes.}, booktitle= {Abstracts of Papers, 249th ACS National Meeting \& Exposition, Denver, CO, United States, March 22-26, 2015}, year= {2015}, publisher= {American Chemical Society}, pages= {ORGN-131}, abstract= {The Merrer group has shown that halocarbene addns. to C-C pi systems with greater than 55 kcal/mol of strain energy (cyclopropene and benzocyclopropene) are influenced by reaction dynamics.  More recently, we have found that pi systems with a lower strain energy - adamantene, 37-40 kcal/mol of strain - are also subject to dynamic control.  To det. if the threshold of strain energy needed to induce dynamic control of carbene addns. is even less than 40 kcal/mol, we currently are studying the reactions of phenylchlorocarbene (PhCCl) with diaryl-fused cyclooctyl pi systems, 1-4.  We are synthesizing 1-4 by amending previously published methods and reacting each compd. with PhCCl generated photochem. from phenylchlorodiazirine.  Product anal. and subsequent computational studies will illuminate the possible intervention of dynamic effects. [on SciFinder(R)]}, note= {Abstracts of Papers, 249th ACS National Meeting \& Exposition, Denver, CO, United States, March 22-26, 2015}}
